mapper.insert:
INSERT INTO BOARD(
	UNQ, TITLE, PASS, NAME, CONTENT, HITS, RDATE
)
VALUES(
	SEQ_BOARD.NEXTVAL, #{title},	#{pass},	#{name},	#{content}, 0, SYSDATE 
)

mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT UNQ, TITLE, NAME, HITS, TO_CHAR(RDATE,'YYYY-MM-DD') RDATE FROM BOARD
WHERE #{searchType} LIKE '%' || #{searchKeyword} || '%'
ORDER BY UNQ DESC) A) B WHERE RN BETWEEN #{rowStart} AND #{rowEnd}
